package com.authapp;import androidx.appcompat.app.AppCompatActivity;import android.app.ProgressDialog;import android.os.AsyncTask;import android.os.Bundle;import android.view.View;import android.widget.EditText;import android.widget.LinearLayout;import android.widget.Toast;import com.authapp.API.APICall;import com.authapp.POJO.AllUser;import com.authapp.POJO.User;import java.util.ArrayList;public class ForgotPasswordActivity extends AppCompatActivity implements View.OnClickListener {    private EditText editUserName;    private LinearLayout btnLogin;    private String susername;    private ArrayList<User> resultList;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_forgot_password);        editUserName = (EditText) findViewById(R.id.etUserName);        btnLogin = (LinearLayout) findViewById(R.id.btnLogin);        btnLogin.setOnClickListener(this);        resultList = new ArrayList<User>();    }    @Override    public void onClick(View v) {        if (v == btnLogin) {            susername = editUserName.getText().toString().trim();            if (susername.equals("")) {                editUserName.setError("Enter Email/ Mobile");                return;            } else {                new PostForgotPassword().execute();            }        }    }    public class PostForgotPassword extends AsyncTask<Void, Void, AllUser> {        private ProgressDialog dialog;        @Override        protected void onPreExecute() {            super.onPreExecute();            dialog = new ProgressDialog(ForgotPasswordActivity.this);            dialog.setMessage("Please Wait..");            dialog.show();            dialog.setCancelable(false);        }        @Override        protected AllUser doInBackground(Void... voids) {            AllUser result = new AllUser();            result = new APICall().ForgotPassword(susername);            return result;        }        @Override        protected void onPostExecute(AllUser allUser) {            super.onPostExecute(allUser);            if (allUser != null) {                resultList.clear();                resultList.addAll(allUser.getData());                String result = resultList.get(0).getResult().toString().trim();                String msg = resultList.get(0).getMsg().toString().trim();                if (result.contains("No")) {                    if (msg.contains("Error")) {                        Toast.makeText(ForgotPasswordActivity.this, "Invalid Email/ Mobile ", Toast.LENGTH_SHORT).show();                    } else if (msg.contains("NoData")) {                        Toast.makeText(ForgotPasswordActivity.this, "Server Connection Error", Toast.LENGTH_SHORT).show();                    }                } else if (result.contains("Yes")) {                    Toast.makeText(ForgotPasswordActivity.this, " Password sent", Toast.LENGTH_LONG).show();                    finish();                }            }            dialog.dismiss();        }    }}